	  var t = Tabs.transport;
for (var o in Seed.buildings[cityId]){
	var buildingType = parseInt(Seed.buildings[cityId][o][0]);
	var buildingLevel = parseInt(Seed.buildings[cityId][o][1]);
	if (buildingType == 12){
		return parseInt(buildingLevel);
		break;
	}
}
	return 0;
