      trAv.appendChild($td([['class', 'tb3chnb'], ["colspan", "2"], ['style', 'text-align:center']], Math.round(totP / totV)));
      if (boolIsMyAlly) { trAv.appendChild($td([['class', 'tb3chnb']])); }
