//////////////////////////////////////////////////////////////////////
function parseCoords(str)
{
   if ( !parseCoords.re )
   {
      parseCoords.re =
       /(?:\(([+-]?\d+).*[|].*?([+-]?\d+)\))|(?:([+-]?\d+)\).*[|].*?\(([+-]?\d+))/;
   }

   var x = null, y = null;
   var matches = parseCoords.re.exec(str);
   
   if ( matches )
   {
      if ( matches[0].charAt(0) === '(' )
      {
         x = parseInt10(matches[1]); 
         y = parseInt10(matches[2]); 
      }
      else
      {
         x = parseInt10(matches[4]); 
         y = parseInt10(matches[3]); 
      }
   }

   

   return [x,y];
}

