				if (availableGeneral == null){
					notify (getErrorText(3,'fb'));
					return;
				}
				var targMsg =  '<B>' + translate('Manual attack sent to') + '</B>: ' + targetObj.n + ' ' +  translate('at') + ' ' + targetObj.x +'/'+ targetObj.y;
				verboseLog(targMsg +' '+ translate('attempted'));

