// ==UserScript==
// @name        Runescape Oldschool - Notepad
// @namespace   Shaun Dreclin
// @include     http://oldschool*.runescape.com*
// ==/UserScript==

var saveGlow = "#00F" // Color to fade text to when data is saved - change to "#000" to disable.

//Fonts by WolfieMario - http://fontstruct.com/fontstructors/wolfiemario - Fonts and images encoded by Spritebaker - http://www.spritebaker.com and DataURL - http://www.dataurl.net
var normalFontData = "data:font/ttf;base64,";
var boldFontData = "data:font/ttf;base64,";
var backgroundData = "data:image/gif;base64,";

//Jquery style document.getElementById
var $ = function(id) { return document.getElementById(id); }

//Create the notepad div, fill it with stuff
var osrNotepad = document.createElement('div');
document.getElementsByTagName("td")[0].style.position = "relative";
document.getElementsByTagName("td")[0].appendChild(osrNotepad);
osrNotepad.id = "osrNotepad";
osrNotepad.innerHTML = '														\
	<div id="osrNotepadHeader">Notepad</div>									\
	<textarea id="osrNotepadText" spellcheck="false"></textarea><br>			\
	<div id="osrNotepadToggle">Notes </div>									\
';

//Apply style
GM_addStyle("																	\
	@font-face {																\
		font-family: runescape;													\
		src: url('" + normalFontData + "') format('truetype');					\
		font-weight: normal;													\
	}																			\
																				\
	@font-face {																\
		font-family: runescape;													\
		src: url('" + boldFontData + "') format('truetype');					\
		font-weight: bold;														\
	}																			\
																				\
	#osrNotepad {																\
		font-family: runescape;													\
		/*background: #F0F;*/													\
		position: absolute;														\
		margin-left: -217px;													\
		top: -530px;															\
		transition: top 1.5s;													\
	}																			\
																				\
	#osrNotepadHeader {															\
		font-family: runescape;													\
		font-weight: bold;														\
		font-size: 16px;														\
		text-shadow: 1px 1px 0px #000;											\
		height: 25px;															\
		padding-top: 3px;														\
		background: url('" + backgroundData + "') no-repeat;					\
	}																			\
																				\
	#osrNotepadToggle {															\
		font-size: 16px;														\
		font-family: runescape;													\
		font-weight: bold;														\
		text-align: right;														\
		cursor: pointer;														\
	}																			\
																				\
	#osrNotepadText {															\
		resize: none;															\
		font-family: runescape;													\
		font-size: 16px;														\
		transition: color 1s;													\
		background: #d0bd9b;													\
		width: 215px;															\
		height: 503px;															\
		margin-top: -2px;														\
		border: 0px;															\
		padding: 5px;															\
		        box-shadow: 0 0 8px #000 inset;									\
		   -moz-box-shadow: 0 0 8px #000 inset;									\
		-webkit-box-shadow: 0 0 8px #000 inset;									\
		border-radius: 3px;														\
");

//open and close toggle
$("osrNotepadToggle").addEventListener("click", function() {
	if($("osrNotepadToggle").innerHTML == "Notes ") {
		$("osrNotepad").style.top = "1px";	
		$("osrNotepadToggle").innerHTML = "Close ";
	} else {
		$("osrNotepad").style.top = "-530px";	
		$("osrNotepadToggle").innerHTML = "Notes ";
	}
});


//Load text from saved file or give it a default value
var osrNotepadText = GM_getValue("osrNotepadText", "Type stuff here!");
$("osrNotepadText").value = osrNotepadText;

//Check for changes in textarea, save text
setInterval(function() {
	$("osrNotepadText").style.color = "#000";
	if(osrNotepadText != $("osrNotepadText").value) {
		osrNotepadText = $("osrNotepadText").value;
		GM_setValue("osrNotepadText", osrNotepadText);
		$("osrNotepadText").style.color = saveGlow;
	}
}, 500);
