//Hide profile sidebar
function hideProfileSidebar () {
	//Hide hideSidebar toogle link
	$(".hideSidebar").remove();
	//Resize main div
	$(".sfgu-profile-sidebar").hide();
	$(".sfgu-profile-content").offset({ left: 10 });
	$(".sfgu-profile-content").width(screen.width-20-18);
	//Resize "Latest Favorites" div
	var carousel_offset = $(".sftc-title").eq(2).width() - 52;
	$(".carousel-wrapper").width(carousel_offset);
	//Resize latest stories/latest music divs
	$(".sf-story").width(500);
	//Rename each story title to the "title" attribute in the href to remove incomplete title with ... at the end
	$(".sf-story-headline a").each( function( index, element ){
		$(this).text($(this).attr("title"));
	});
	//Rename each autor's name in story divs to full names to remove ... at the end
	$(".sf-story-additional .sfTextAttention").each( function( index, element ){
		$(this).text($(this).attr("title"));
	});
}

