					row["claim"] = (item.hasBaron) ? item.claim : "";
					
					row["attackerCityId"] = item.city;
					row["attackerPosition"] = bos.Utils.convertIdToCoodrinates(item.city);
					row["attackerCityName"] = item.cityName;
									
