			container.add(this.btnCalc);
			this.btnCalc.addListener("click", this.calculateFoodConsumption, this);
			
			this.btnRevCalc = new qx.ui.form.Button(locale == "de" ? "Nahrung zu Einheiten" : (tr("Cibo -> Unità")));
			this.btnRevCalc.setToolTipText(locale == "de" ? "Berechnet die maximale Einheitenzahl fÃƒÂ¼r die eingegebene Nahrungsmenge" : "Calcola il numero max di unità militari a seconda della quantita' di cibo inserito");
			this.btnRevCalc.setWidth(150);
