						webfrontend.net.CommandManager.getInstance().sendCommand("UpgradeBuilding", {
							cityid : webfrontend.data.City.getInstance().getId(),
							buildingid : bldngId,
							buildingtype : type,
							isPaid : true
						}, null, function() {
						});
