						actionLog(""+culang.mainTransport+"",""+culang.transportAutoLog+" " + cityname + " "+culang.transportAutoLog2+" " + xcoord + ',' + ycoord + toCityName + "  -> "+ unsafeWindow.unitcost[unit][0] +": " + wagons_needed);
