						t.dispTrainStatus ('<font color=#550000><B>'+culang.trainDeleteMoreTrain+'</b></font><BR>');
						document.getElementById ('KsCancelAllTrain1').style.display='inline';
						document.getElementById ('KsCancelAllTrain2').style.display='inline';
						t.displayCityStats();
