                        maxDamage                = 0,
                        title                    = '',
                        id                       = '',
                        monsterObjLink           = '',
                        visitMonsterLink         = '',
                        visitMonsterInstructions = '',
                        removeLink               = '',
                        removeLinkInstructions   = '',
                        len                      = 0,
                        data                     = {text: '', color: '', bgcolor: '', id: '', title: ''},
                        linkRegExp               = new RegExp("'(http.+)'"),
                        duration                 = 0,
