////////Player ID//////////////////////
var playerID = new Array;

function updatePlayersID(){

	var invocation = new XMLHttpRequest();
	var url = 'http://www.frikipremier.com/cron/script.php';
	
	//alert( "Actualizando lista de jugadores..." );
	
	if( invocation ){
	
		invocation.open('GET', url, false);
		invocation.send(); 
		var response = invocation.responseXML;
		var players = response.getElementsByTagName('jugador');
		
		var playerString = "";
		
		for (var i=0; i<players.length; i++){
		
			name = players[i].getElementsByTagName("nombre")[0].childNodes[0].nodeValue;
			id = players[i].getElementsByTagName("id")[0].childNodes[0].nodeValue;
			playerString += trim( name ) + "//" + id + "//";
		}
		localStorage.setItem("playerID", playerString);
		//alert( "Lista actualizada con exito ( " + players.length + " jugadores en la lista )" );
	}
	else{
		alert("Datos no disponibles. Visitar http://www.frikipremier.com");
	}
	return;
}

function readPlayersID(){

	var playerString;
	
	try{
		playerString = localStorage.getItem("playerID").split("//");
	}
	catch(ex){
		updatePlayersID();
		playerString = localStorage.getItem("playerID").split("//");
	}
	
	
	for( var i=0; i<playerString.length; i+=2 ){
		playerID[ playerString[i] ] = playerString[i+1];
	}
	return;
}

readPlayersID();


