var version = "0.5.2";	//Version of the script. For checking for updates
var update;				//Variable for storing the result of checking for updates
var plusPlayer = false;	//Variable to store if we are plusPlayer
var get;				//To store the get function

//Function get (it gets data from other web)
//url--> url of the web
//func--> function to do with the received data

try{
	//Try to assign GM_xmlhttpRequest to get function (firefox and emulated greasemonkey). It can do cross domain!
	get = function(url, func) {
	  GM_xmlhttpRequest({
		method: "GET",
		 url: url,
		 onload: function(xhr) { func(xhr.responseText); }
	  });
	}
}
catch( ex ){
	//If we cant with GM_xmlhttpRequest, assign general request (it cant do cross domain...)
	get = function(url, func) {
		var xhr = new XMLHttpRequest();
		xhr.open("GET", url, true);
		xhr.send();
		xhr.onreadystatechange = function() {
		  if (xhr.readyState == 4) {
			func(xhr.responseText);
		  }
		}
	}
}

	
//Function checkUpdate (it checks if there is a new version)
//text--> Data from the web where it is the updated script
function checkUpdate(text) {

	var ini = text.indexOf("<b>Version:</b>");	//Search for number of version ( x.x.x )
	ini += ("<b>Version:</b>").length;
	var end = text.indexOf( "<br />", ini );

	text = text.substring( ini, end );
	text = text.replace( /\s/g, "" );		//Clear white spaces
	
	if( text.split(".").length < 3 ){	//Error if there are not at least 3 numbers
		update = '<tr><td><span class="button01">Error al comprobar la versin</span></td></tr>';
	}
	else if( text == version ){				//Using the newest version
		update = '<tr><td><span class="button01">Versin actualizada</span></td></tr>';
	}
	else{	//New update!
		update = '<tr><td><span class="button01">Versin vieja</span></td></tr>' + 
				 '<tr><td><a href="http://userscripts.org/scripts/show/88692"> Actualizar!</a></td></tr>'	
	}
	
	//Search for status player and change it to FrikipremierPlayer with the number of version of the script
	var span = document.getElementsByTagName("span");
	for( var i=0; i<span.length; i++){

		//plusplayer?
		if (span[i].textContent == "Plus" ){
			plusPlayer = true;
		}
		//or basic?
		if (span[i].textContent == "basic" ){
			span[i].textContent = "Frikipremier";
			
			//add it!
			span[i].parentNode.parentNode.parentNode.innerHTML += '<tr><td><span class="button01">v' + version + '</span></td></tr>' + update;
			break;
		}
	}
}
 
