		function getConv(text){
		
			link3.textContent = link3.textContent.replace( "*", "" );
			
			var td = document.getElementById("convCol");
		
			var ini = text.indexOf('<span class="postbody">');
			var end = text.indexOf('<br />_________________<br />');
			
			var arrayPost = new Array;
			var i = 0;
			
			while( ini != -1 ){
			
				arrayPost[i] = '<table>' + text.substring( ini, end ) + '<br><br></table>';
				
				td.innerHTML += arrayPost[i];
				
				i++;
				ini = text.indexOf('<span class="postbody">', ini+1);
				end = text.indexOf('<br />_________________<br />', end+1);
			
			}
		}
		
	
