		function getPoints(text){
		
			link1.textContent = link1.textContent.replace( "*", "" );
			
			var teamsTable = document.getElementById("teamsTable");
			var teamRow, newRow;
			var teamNum = 0;
			var tablePoints;
			var end = -1;
			var ini = text.indexOf( '<span class="postbody">' );
			end = text.indexOf( '<br />_________________<br />', ini+1 );
			
			if( text.substring( ini, end ).split(/<br \/>/).length < 10 ){
				ini = text.indexOf( '<span class="postbody">', ini+1 );
			}

					
			while( ini != -1 ){
			
				end = text.indexOf( '<br />_________________<br />', ini+1 );
				var line = text.substring( ini, end ).replace( /<\/?span>/gi, "" ).split(/<br \/>/);
				ini = text.indexOf( '<span class="postbody">', end+1 );
				
				tablePoints = document.createElement("table");
				tablePoints.style.display = "none";
				
				var first = true;
				for( var i=0; i<line.length; i++ ){
				
					if( line[i].replace( /\s/gi, "" ) == "" ){
						continue;
					}
					if( first ){
						first = false;
						line[i] = line[i].replace( /<[^<>]+>/gi , "" );
						teamRow = teamsTable.insertRow(-1);
						teamRow.innerHTML = '<td align="left"><a href="JavaScript:;"><span style="color: orange">' + line[i] + '</span></a></td>';
						teamRow.getElementsByTagName("a")[0].setAttribute("teamNum", teamNum);
					}
					else{
						newRow = tablePoints.insertRow(-1);
						newRow.innerHTML = '<td align="left"><a href="JavaScript:;">' + line[i] + '</a></td>';
						newRow.getElementsByTagName("a")[0].addEventListener( "click", function(){ addPlayerPoints(this.parentNode.parentNode);}, false);
					}
				}
				newRow = teamsTable.insertRow(-1).insertCell(-1);
				newRow.appendChild(tablePoints);
				
				teamRow.getElementsByTagName("a")[0].addEventListener( "click", function(){toggleDisplay(this.parentNode.parentNode.parentNode.getElementsByTagName("table")[this.getAttribute("teamNum")]);}, false  );
				teamNum++;
			}
		}
	
		function getURLPoints(text){

			var ini = 0;
			var end = 0;
			var iniTitle = 0;
			var endTitle = 0;
			var count = 0;
		
			while( ini != -1 && count < 100 ){
			
				ini = text.indexOf( 'href="viewtopic', end ) + ( ('href="').length );
				end = text.indexOf( '"', ini );
				
				iniTitle = text.indexOf( '" class="topictitle">', end ) + '" class="topictitle">'.length;
				endTitle = text.indexOf( '</a>', iniTitle );
				
				if( iniTitle - end > '" class="topictitle">'.length ){
					count++;
					continue;
				}
				
				if( text.substring( iniTitle, endTitle ).toLowerCase().indexOf("puntos") != -1 ){
				
					get( window.location.href.replace("lineup.phtml", "" ) + "external/phpBB2/" + text.substring( ini, end ), getPoints );
					break;
				}
				count++;
			}
			
			if( ini == -1 ){
				get( window.location.href.replace( "lineup.phtml", "" ) + 'external/phpBB2/viewforum.php?f=11', getURLNoPlayers );
			}

		}
		
