				var i,ii,range_i;
				var ret;
				if(next){
					for(i=0,ii=range.length,range_i;i<ii;i++){
						range_i=range[i];
						if(range_i>cur){
							ret=range_i;
							break;
						}
					};
				}else{
					for(i=range.length-1;i>=0;i--){
						range_i=range[i];
						if(range_i<cur){
							ret=range_i;
							break;
						};
					};
				};
