    doReassign: function (count) {
        var t = Tabs.Reassign;
        t.notBusy = false;
        if (t.reassignRoutes.length == 0) return;
        var city = t.reassignRoutes[count].city;
        if (!Cities.byID[city]) return;
        var cityIdx = parseInt(Cities.byID[city].idx);
        var cityname = Cities.byID[city].name;
        var citytotal = 0;
		var makeMarch = {};
		var marchBase = t.getMarchBase(city, t.reassignRoutes[count].target_x, t.reassignRoutes[count].target_y);
		var setTroops = [];
		for (var i = 0; i < untTypTot; i++) setTroops[i]=0; //array by index, not object
        var cityID = 'city' + city;
        var marching = getMarchInfo(cityID);
        var maxsend = getMaxMarchSize(city);
		var totalsend = 0;
		for (var k = 1; k <= untTypTot; k++) {			
            var citytroops = 0;
            var sendtroops = 0;
            var nameNS = TroopInfo.idx[k-1].nameNS;
			if (DEBUG_TRACE) actionLog(" Checking " + nameNS + ' ');
            if (t.reassignRoutes[count]['Send' + nameNS] == false) continue;
            citytroops = parseInt(Seed.units[cityID]['unt'+k])*1;
			if (isNaN(citytroops)) continue;
            var marchtroops = 0;
            marchtroops = parseInt(marching.marchUnits[k])*1;
			if (isNaN(marchtroops)) continue;
			if (DEBUG_TRACE) actionLog('There are '+ marchtroops + ' marching and '+ citytroops + ' in the city ');
            citytotal = parseInt(citytroops) + parseInt(marchtroops);
            if (DEBUG_TRACE) actionLog(cityname + " has " + citytotal + ' ' + nameNS + ' and number to keep is ' + t.reassignRoutes[count][nameNS]);
            if (citytotal > parseInt(t.reassignRoutes[count][nameNS])) {
                sendtroops = parseInt(citytotal) - parseInt(t.reassignRoutes[count][nameNS]);
                var availableSpace = parseInt(maxsend) - parseInt(totalsend);
				if (parseInt(sendtroops) < 0) sendtroops = 0;
				if (parseInt(sendtroops) > parseInt(citytroops)) sendtroops = citytroops;
				if (DEBUG_TRACE) actionLog("Number of troops to send: " + sendtroops + ' availableSpace  is ' + availableSpace + '  ');
				if (sendtroops > availableSpace) {
					setTroops[k-1] = availableSpace;
					if (DEBUG_TRACE) actionLog('Have added '+ setTroops[k-1] + ' ' + nameNS + ' to "setTroops" ' + setTroops );
//					var firstMarch = marchBase(setTroops);
					makeMarch = marchBase(setTroops);
					MarchQueue[cityIdx + 1].unshift(makeMarch);
					for (var i=0;i<k-1;i++) setTroops[i]=0; //march has been queued, reset previous troops from current back to zero
					sendtroops -= availableSpace;
					if (DEBUG_TRACE) actionLog('Reassign has been queued for maximum march size and now number left to send is ' + sendtroops + '  ');
					if (sendtroops > maxsend) {
						setTroops[k-1] = maxsend;
//						var maxRPmarch = = marchBase(setTroops);
						makeMarch = marchBase(setTroops);
						while (sendtroops > maxsend){
							MarchQueue[cityIdx + 1].unshift(makeMarch);
							sendtroops -= maxsend;
							if (DEBUG_TRACE) actionLog(maxsend + ' queued, now number left to send is ' + sendtroops + '  ');
						}
					}
					totalsend = 0;
				} 
				setTroops[k-1] = sendtroops;
				if (DEBUG_TRACE) actionLog('Need to send '+ sendtroops + ' '+ nameNS + ' ');
				totalsend += sendtroops;
				sendtroops = 0;
				if (DEBUG_TRACE) actionLog('Still room for more troops, checking next troop type');
            }
        }
		if (DEBUG_TRACE) actionLog('Still room for more troops, checking next troop type');
        if (totalsend > 0) {
//			var lastMarch = marchBase(setTroops);
			makeMarch = marchBase(setTroops);
			MarchQueue[cityIdx + 1].unshift(makeMarch);
		}
        if (DEBUG_TRACE) actionLog('Done with route ' + count + ' ');
		t.notBusy = true;
    },
	getMarchBase: function(city,targetX,targetY){
		return function(setTroops){
		var makeMarch={};
		for (var i = 0; i < untTypTot; i++) makeMarch[TroopInfo.idx[i].byNum]=setTroops[i];
		makeMarch.what = "Reassign";
		makeMarch.city = city;
		makeMarch.action = 5;
		makeMarch.targetX = targetX;
		makeMarch.targetY = targetY;
		for (var i = 1; i <= 5; i++) makeMarch['r'+i]=0;
		makeMarch.gold = 0;
		return makeMarch;
		}
	},
