    PaintMarchSize : function (){
    	var t = Tabs.March;
    	var Aura = 0;
    	if (document.getElementById('AuraOfCommand').checked) Aura = 25;
    	if (document.getElementById('AuraOfConquest').checked) Aura = 50;
   		t.rallypointlevel=t.getRallypoint(t.from.city.id);
    	RallypointMax = (t.rallypointlevel * 10000);
    	var TR = unsafeWindow.cm.ThroneController.effectBonus(66);
    	if (TR > 150) TR=150;
    	t.maxsend = RallypointMax + Math.floor(RallypointMax * (TR / 100));
		var prestigeRallyPercent = 1.40;
    	if (Seed.cityData.city[t.from.city.id].isPrestigeCity) {
			for (var i=1;i<=6;i++){
				i <= 2 ? prestigeRallyPercent += 0.10 : prestigeRallyPercent += 0.05;
				if (i == (parseInt(Seed.cityData.city[t.from.city.id].prestigeInfo.prestigeLevel))) { t.maxsend = Math.floor(t.maxsend * prestigeRallyPercent);	break;}			
			}
    		for (var bl=0;bl<Seed.cityData.city[t.from.city.id].prestigeInfo.blessings.length;bl++) if (Seed.cityData.city[t.from.city.id].prestigeInfo.blessings[bl]==207) t.maxsend = Math.floor(t.maxsend * 1.10);
    	}
    	t.maxsend = t.maxsend * (1 + (Aura/100));  	
    	var m = 'March Size: ' + addCommas(Math.floor(t.maxsend)) + ' (TR: ' + TR + '% - Aura: ' + Aura + '%)';
    	document.getElementById('MPresetCont').innerHTML = m;
    },
