				if (Seed.queue_unt['city'+Seed.cities[ii][0]]!=null && Seed.queue_unt['city'+Seed.cities[ii][0]].length>0) TroopTime = Seed.queue_unt['city'+Seed.cities[ii][0]][Seed.queue_unt['city'+Seed.cities[ii][0]].length-1][3] - now;
