        var quality = parseInt(y["quality"]);
      if (i<=quality) m+='<TR><TD><FONT color=black>' + Math.round(Current*100)/100 + "% " + unsafeWindow.cm.thronestats["effects"][id]["1"] + '</font></td></tr>';
