	calcMightloss(rslt.fght,0)
	m +='<BR>Might lost: ' + addCommas(Mightloss);
	m +='<BR>Wounded might: ' + addCommas(WoundedMight);
	m +='<BR>Rounds: ' + rslt.rnds;
