var AutoUpdater_174804 = {
    id: 174804,
    version: Version,
    call: function(secure) {
        GM_xmlhttpRequest({
            method: 'GET',
        url: '//userscripts.org/scripts/source/'+this.id+'.meta.js',
        onload: function(xpr) {AutoUpdater_174804.compare(xpr);},
            onerror: function(xpr) {if (secure) AutoUpdater_174804.call(false);}
        });
    },
    compareVersion: function(r_version, l_version) {
    	r_version = r_version.substr(5);
    	l_version = l_version.substr(5);
        return (r_version !== l_version) ? r_version > l_version : false;
    },
    compare: function(xpr) {
        this.xversion=/\/\/\s*@version\s+(.+)\s*\n/i.exec(xpr.responseText);   
        if (this.xversion) this.xversion = this.xversion[1];
         else return; 
        var updated = this.compareVersion(this.xversion, this.version);   
        if (updated) {                  
 			var body = '<BR><DIV align=center><FONT size=3><B>New version ' + this.xversion + ' is available!</b></font></div><BR>';
 			body+='<BR><iframe style=";border:0;width:475px;max-width:475px;height:150px;max-height:150px;overflow:auto" src="//nicodebelder.eu/koc/aio/changelog.html"></iframe><BR>';
 			body+='<BR><DIV align=center><a class="gemButtonv2 green" id="doBotUpdate">Update</a></div>';
 			ShowUpdate(body);
        } 
    },
    check: function() {
    	var now = unixTime();
    	var lastCheck = 0;
    	if (localStorage.updated_174804) lastCheck = parseInt(localStorage.updated_174804);
		if (now > (lastCheck + 60*60*2)) this.call(true);
    }
};
