function FillMarchQueue(){
	mQfilling = true;
	for (var ii=0;ii<Seed.cities.length;ii++){
		cityID = Seed.cities[ii][0];	
		var rallypointlevel = Cities.byID[cityID].rpLevel;
		var now = unixTime();
		if (MarchQueue[ii+1].length < rallypointlevel) {
			if (DEBUG_TRACE) actionLog("Check Transport");
			if (Options.lastCityTransport[ii+1]>((Options.transportinterval * 60) + now)) Options.lastCityTransport[ii+1] = ((Options.transportinterval * 60) + now);
			if (Tabs.transport.traderState.running && (Options.lastCityTransport[ii+1]+(Options.transportinterval * 60)) < now) Tabs.transport.doMarch(cityID,ii);
			if (DEBUG_TRACE) actionLog("Check DF");
			if (now < (Tabs.DF.mapError + 60) && Tabs.DF.justBogs[0]) $('#MapInfo').text(" MAP ERROR! - Switching Dark Forest Logic ").css('color', 'red');
			if (DEBUG_TRACE) actionLog("Map Error Check");
			if (DFOptions.Running) var send = Tabs.DF.doSearch(cityID,ii); 
			if (send) continue;
			if (AtkOptions.Running) Tabs.Attack.doMarch(cityID,ii);
			if (FarmOptions.Running) Tabs.farm.barbing(cityID,ii);
			if (CrestOptions.Running) {				
				for (var k in Seed.wilderness['city'+cityID]) if (Seed.wilderness['city'+cityID][k]['xCoord']==CrestOptions.X && Seed.wilderness['city'+cityID][k]['yCoord']==CrestOptions.Y) Tabs.Crest.abandonWilderness(Seed.wilderness['city'+cityID][k]['tileId'],Seed.wilderness['city'+cityID][k]['xCoord'],Seed.wilderness['city'+cityID][k]['yCoord'],CrestOptions.CrestCity);
				Tabs.Crest.FirstRound();
			}
		}
	}
	mQfilling = false;
}
function CheckMarchQueue(){
	mQchecking=true;
	var RP = getRallypoint(Seed.cities[WhatCity-1][0]);
	var numCities = Cities.numCities;
	var MarchesRunning = CheckCityMarches(WhatCity-1);
	var total = 0;
	for (var i=1;i<=numCities;i++) total += MarchQueue[i].length;
	if (total == 0) {
		mQchecking=false;
		return;
		}
	if (MarchQueue[WhatCity].length > 0) {
		if (MarchesRunning < (RP-Options.RPClip)) doMarch(MarchQueue[WhatCity][0],WhatCity);
		WhatCity++;
		if (WhatCity > numCities) WhatCity = 1;
	}
	else {
		WhatCity++;
		if (WhatCity > numCities) WhatCity = 1;
		CheckMarchQueue();
	}
	mQchecking=false;
}
function areTroopsOK(action){
	var TroopsOK = 0;
	    for (var i=0;i<untTypTot;i++){
    	var TroopsInCity = Seed.units['city'+action.city][TroopInfo.idx[i].id];
    	if (action[TroopInfo.idx[i].byNum]) {
    		if (parseInt(TroopsInCity) < parseInt(action[TroopInfo.idx[i].byNum])) TroopsOK += 1;
    	}
    }
	    if (TroopsOK > 1) return false;
}
function CheckCityMarches(City) {
    var Counter = 0;
    var cityID = Seed.cities[City][0];
    var now = unixTime();
    if (Seed.queue_atkp['city' + cityID] != undefined){
        for (atkp in Seed.queue_atkp['city' + cityID]){
            if (Seed.queue_atkp['city' + cityID][atkp]["marchId"] || Seed.queue_atkp['city' + cityID][atkp]["returnUnixTime"] > now){
				if (Seed.queue_atkp['city' + cityID][atkp]["marchStatus"] != 10) Counter++;
			}
		}
	}
	return Counter;
}
