// -- Search redirect --------------------------------------------------------------------------------------------------
function searchRedirect() {
	if (eRAopt['search'] == false) {
		return;
	}
	
	var entities = document.getElementsByClassName("entity")
	if (entities.length == 1) {
		entities[0].parentNode.parentNode.parentNode.innerHTML += "<tr><td colspan='4' style='text-align:center;padding:10px'>...</td></tr>"
		location.assign(entities[0].getElementsByTagName("a")[0].href);
	}
}
