function sgSwitchVisibility() {
	if(document.getElementById("sg_menu").style.display == 'none') {
		document.getElementById("sg_menu").style.display = 'block';
		GM_setValue("sg_menu_display", 'block');
	} else {
		document.getElementById('sg_menu').style.display = 'none';
		GM_setValue('sg_menu_display', 'none');
	}
}

sgMenu();