							if(rslt.updateSeed){uW.update_seed(rslt.updateSeed)};
							ById('pbSrcScoutResult').innerHTML += ''+culang.searchScoutSent+'<BR>';
							if (notify)
								setTimeout(function(){ notify(count+1); }, 1500);
