		if (isNaN(slots) || slots<0)
			slots = 0;
		t.TTspMax.innerHTML = t.stats.MaxTrain;
		if (id <=12)
			t.TTspMaxSlots.innerHTML = t.stats.barracks1 - t.stats.queued1;
		else 
			t.TTspMaxSlots.innerHTML = t.stats.barracks2 - t.stats.queued2;
			
