function inspect(obj, maxLevels, level, doFunctions){
	var str = '', type, msg;
	if(level == null)  level = 0;
	if(maxLevels == null) maxLevels = 1;
	if(maxLevels < 1)
		return 'Inspect Error: Levels number must be > 0';
	if(obj == null)
		return 'ERROR: Object is NULL\n';
	var indent = '';
	for (var i=0; i<level; i++)
		indent += '  ';
	for(property in obj) {try {type =  matTypeof(obj[property]);if (doFunctions==true && (type == 'function')){str += indent + '(' + type + ') ' + property + "[FUNCTION]\n   " + ( (obj[property]==null)?(': null'):('')) +' = '+ obj[property] +"\n";} else if (type != 'function') {str += indent + '(' + type + ') ' + property + ( (obj[property]==null)?(': null'):('')) +' = '+ obj[property] +"\n";}
			if((type=='object' || type=='array') && (obj[property] != null) && (level+1 < maxLevels))
				str += inspect(obj[property], maxLevels, level+1, doFunctions);  // recurse
		}catch(err) {if(typeof(err) == 'string') msg = err;else if(err.message)        msg = err.message;else if(err.description)    msg = err.description;else msg = ''+culang.mainUnknown+'';str += '(Error) ' + property + ': ' + msg +"\n";}
	}
	str += "\n";return str;
}

