    CheckForAlerts: function (){
    	var t = Dashboard;
    	var foodLev = DashboardOptions.foodLevel
    	var woodLev = DashboardOptions.woodLevel
    	var stoneLev = DashboardOptions.stoneLevel
    	var oreLev = DashboardOptions.oreLevel
    	var astoneLev = DashboardOptions.astoneLevel
    	t.Alerts = [];
		for (var a=1; a<=5;a++) for(b=0; b<Cities.numCities; b++) {
			if (a==5) rec = Seed.resources["city" + Seed.cities[b][0]]['rec'+a][0];
				else rec = Math.round(Seed.resources["city" + Seed.cities[b][0]]['rec'+a][0]/3600);
    		switch (a){
    			case 1:if (rec < foodLev) t.Alerts.push(Seed.cities[b][1] + " (" + (b+1) +")"+culang.dashboard_alert_foodalert+" " + addCommas(rec));break;
    			case 2:	if (rec < woodLev) t.Alerts.push(Seed.cities[b][1] + " (" + (b+1) + ")"+culang.dashboard_alert_woodalert+" " + addCommas(rec));break;
    			case 3:	if (rec < stoneLev) t.Alerts.push(Seed.cities[b][1] + " (" + (b+1) + ")"+culang.dashboard_alert_stonealert+" " + addCommas(rec));break;
    			case 4:	if (rec < oreLev) t.Alerts.push(Seed.cities[b][1] + " (" + (b+1) + ")"+culang.dashboard_alert_orealert+" " + addCommas(rec));break;
    			case 5:	if (rec < astoneLev) t.Alerts.push(Seed.cities[b][1] + " (" + (b+1) + ")"+culang.dashboard_alert_aehteralert+" " + addCommas(rec));break;
    		}
    	}
    	for(b=0; b<Seed.cities.length; b++) if (Seed.queue_unt["city" + Seed.cities[b][0]].length == 0) t.Alerts.push(Seed.cities[b][1] +  " (" + (b+1) + ")"+culang.dashboard_alert_notroopstrain+"");

    	for(b=0; b<Seed.cities.length; b++) {
    		var check = false;
    		var tech = false;
    		var tech2 = false;
    		for (k in Seed.buildings["city" + Seed.cities[b][0]])	{
    			if (Seed.buildings["city" + Seed.cities[b][0]][k][1] < 9 && Seed.buildings["city" + Seed.cities[b][0]][k][2] < 500) if (Seed.queue_con["city" + Seed.cities[b][0]].length == 0 && Tabs.build["bQ_" + Seed.cities[b][0]].length == 0) check = true;
    			if (Seed.buildings["city" + Seed.cities[b][0]][k][0] == 11 && Seed.queue_tch["city" + Seed.cities[b][0]].length == 0) tech = true;
    			if (Seed.buildings["city" + Seed.cities[b][0]][k][0] == 27 && Seed.queue_tch2["city" + Seed.cities[b][0]].length == 0) tech2 = true;
    		}
    		if (check) t.Alerts.push(Seed.cities[b][1] +  " (" + (b+1) + ")"+culang.dashboard_alert_nobuildqueue+"");
    		if (tech) t.Alerts.push(Seed.cities[b][1] +  " (" + (b+1) + ")"+culang.dashboard_alert_noreseach+"");
    		if (tech2) t.Alerts.push(Seed.cities[b][1] +  " (" + (b+1) + ")"+culang.dashboard_alert_nobritonreseach+"");
    	}
    	
    	var count = 0;
    	for (k in uW.kocThroneItems) count++;
    	if (count > (Seed.throne.rowNum*5)) t.Alerts.push(''+culang.dashboard_alert_thronefull+' (' + count + '/' + (Seed.throne.rowNum*5) +')');
    	
    	var msg="";
    	for (k=0;k<t.Alerts.length;k++) msg+= t.Alerts[k] + '<BR>';
    	if (t.Alerts.length > 0) {
    		document.getElementById('AlertsPlaceholder').innerHTML = '<DIV class=pdxStat><a href="#" onclick="KsSaveCategorys(\'powerDashWarings\');">'+culang.mainAlert+'</a></div><DIV id="powerDashWarings" style="display:'+Options["powerDashWarings"]+';"><DIV id=ShowAlerts></div></div>'; //style="height: 60px; overflow: auto"
    		document.getElementById('ShowAlerts').innerHTML = msg;	
    	} else document.getElementById('AlertsPlaceholder').innerHTML = "";
    },
   

