function getCityBuilding (cityId, buildingId){
	var b = Seed.buildings['city'+cityId];
	var ret = {count:0, maxLevel:0};
	for (var i in b) {
		var building = b[i];
		if (building[0] == buildingId){
			++ret.count;
			if (parseInt(building[1]) > ret.maxLevel)
				ret.maxLevel = parseInt(building[1]);
		}
	}
	return ret;
}
