	MakeBlocks: function (getX, getY, city, i, length) {
		var t = Tabs.WildAttack;
		var blocks = [];
		var xx = 0;
		var yy = 0;
		for (x = (getX - WildOptions.Radius); x <= (getX + WildOptions.Radius); x += 5) {
			for (y = (getY - WildOptions.Radius); y <= (getY + WildOptions.Radius); y += 5) {
				xx = x;
				yy = y;
				if (x > 750) xx -= 750;
				if (y > 750) yy -= 750;
				if (x < 0) xx += 750;
				if (y < 0) yy += 750;
				blocks.push("bl_" + xx + "_bt_" + yy);
			}
		}
		t.doSearch(blocks, getX, getY, city, i, length)
	},
	doSearch: function (pass, xx, yy, city, i, length) {
		var t = Tabs.WildAttack;
		var mapDat = [];
		var params = uW.Object.clone(uW.g_ajaxparams);
		params.blocks = pass;

		new AjaxRequest(uW.g_ajaxpath + "ajax/fetchMapTiles.php" + uW.g_ajaxsuffix, {
			method: "post",
			parameters: params,
			onSuccess: function (transport) {
				var rslt = eval("(" + transport.responseText + ")");
				if (rslt.updateSeed) {try { uW.update_seed(rslt.updateSeed); } catch (e) {}}
				if (rslt.ok) {
					if (rslt.data == []) alert("MAP ERROR");
					if (i < length) ById('searchWildProgress').value = i;
					else ById('WildStats').innerHTML = "";
					for (y in rslt.data) {
						var e = rslt.data[y];
						if ((WildOptions.Types.grassland && e.tileType == 10) ||
								(WildOptions.Types.lake && e.tileType == 11) ||
								(WildOptions.Types.woods && e.tileType == 20) ||
								(WildOptions.Types.hills && e.tileType == 30) ||
								(WildOptions.Types.mountain && e.tileType == 40) ||
								(WildOptions.Types.plain && e.tileType == 50)) {
							if (WildOptions.Levels[city][parseInt(e.tileLevel)]) {
								//Frei?
								if (e.tileUserId == null && e.misted == false) {
									mapDat.push({
										X: parseInt(e.xCoord),
										Y: parseInt(e.yCoord),
										L: parseInt(e.tileLevel),
										T: parseInt(e.tileType),
										D: distance(xx, yy, parseInt(e.xCoord), parseInt(e.yCoord)),									
									});
									WildOptions.WildFound++;
								}
							}
						}
					}
					saveWildOptions();
					GM_setValue('WildAttack_' + Seed.player['name'] + '_city_' + city + '_' + getServerId(), JSON2.stringify(mapDat));
					var helpArray = JSON2.parse(GM_getValue('WildAttack_' + Seed.player['name'] + '_city_' + city + '_' + getServerId(), "[]"));
					if (helpArray) t.WildArray[(city - 1)] = helpArray.sort(function sortBarbs(a, b) {
						a = a['D'];
						b = b['D'];
						return a == b ? 0 : (a < b ? -1 : 1);
					});
					if (i < length) ById('searchWildProgress').value = i;
					else ById('WildStats').innerHTML = "<b>" + culang.wildLastSearch + "</b> <i><span class='boldRed'>" + FullDateTime(WildOptions.LastSearch) + "</span></i> <b>" + culang.wildLastSearch2 + "</b> <i><span class='boldRed'>" + WildOptions.WildFound + "</span> " + culang.wildLastSearch3 + "</i>";
				}
			},
		});
	},
