// ==UserScript==
// @name        Web QQ Sound Replace
// @namespace   http://userscripts.org/users/ts
// @description 自定义网页 QQ 中的消息提示音
// @include     http://web*.qq.com/*
// @version     1.1
// @copyright   程序代码以GNU GPL v3或CC by-sa 3.0发布，但请注意程序中涉及的音频文件的相关版权可能归原作者所有
// @author      田生
// @updateURL   http://userscripts.org/scripts/source/404753.meta.js
// @downloadURL http://userscripts.org/scripts/source/404753.user.js
// @grant       unsafeWindow
// @grant       GM_getValue
// @grant       GM_setValue
// @grant       GM_addStyle
// ==/UserScript==

(function () {

  // 只在顶层窗口上工作
  if (window.top !== window.self) return;

  var files = {
    'qq_msg': 'data:audio/ogg;base64,',
    'tm_msg': 'data:audio/ogg;base64,',
    'audio' : 'data:audio/ogg;base64,',
    'qq_sys': 'data:audio/ogg;base64,',
    'tm_sys': 'data:audio/ogg;base64,',
    'mute'  : 'data:audio/ogg;base64,',
  };

  var sounds = {
    'msg': {
      'text': '聊天消息',
      'web': './sound/msg.mp3',
      'qq': files.qq_msg,
      'tm': files.tm_msg,
      'mute': files.mute,
    },
    'audio': {
      'text': '语音视频',
      'web': './sound/Audio.mp3',
      'qq': files.audio,
      'tm': files.audio,
      'mute': files.mute,
    },
    'system': {
      'text': '系统消息',
      'web': './sound/system.mp3',
      'qq': files.qq_sys,
      'tm': files.tm_sys,
      'mute': files.mute,
    },
  };

  var types = {
    'mute': '(静音)',
    'web': '网页QQ',
    'qq': '桌面QQ',
    'tm': '桌面TM',
    'def': '自定义',
  };

  // 保存设置
  var saveConfig = function (name, chose, sound) {
    sounds[name].chose = chose;
    if (Object.keys(sounds[name]).indexOf(chose) !== -1) sounds[name].sound = sounds[name][chose];
    else sounds[name].sound = sound;
    GM_setValue('S' + name, sounds[name].sound);
    GM_setValue('O' + name, sounds[name].chose);
  };

  // 读取设置
  (function loadConfig() {
    Object.keys(sounds).map(function (name) {
      sounds[name].sound = GM_getValue('S' + name, null) || sounds[name].web;
      sounds[name].chose = GM_getValue('O' + name, null) || 'web';
      if (Object.keys(types).indexOf(sounds[name].chose) === -1) saveConfig(name, 'web');
    });
  }());

  var playAudio = (function () {
    var audios = {};
    return function (url) {
      if (audios[url]) return;
      var audio = new Audio(url); audio.play();
      audios[url] = audio;
      audio.addEventListener('ended', function () { audios[url] = null; });
    };
  }());

  // 检查并替换对应的声音
  var addSoundHandler = function () {
    if (!unsafeWindow.alloy.sound || !unsafeWindow.alloy.sound.playSound)
      return setTimeout(addSoundHandler, 1);
    var oldPlaySound = unsafeWindow.alloy.sound.playSound;
    unsafeWindow.alloy.sound.playSound = function (arg, arg2) {
      if (unsafeWindow.alloy.sound.isMute()) return;
      var uri = arg;
      Object.keys(sounds).map(function (type) {
        if (arg === sounds[type].web)
          uri = sounds[type].sound;
      });
      var ret, fail = false;
      // 尝试用WebQQ提供的Flash进行播放
      try {
        ret = oldPlaySound.apply(this, [uri, arg2]);
        if (!ret || (ret._fid && ret._fid < 0)) throw '';
      }
      // 如果不能正常播放，尝试使用替代的audio元素进行播放
      catch (e) {
        try {
          if (arg2) playAudio(uri);
          ret._url = null;
        } catch (e2) { fail = true; }
      }
      return ret;
    };
  };

  // 播放声音
  var playSound = function (name) {
    unsafeWindow.alloy.sound.playSound(sounds[name].sound, true);
  };

  // 向界面中添加设置选项
  var addOptions = function addOptions() {
    var specificAppSection = document.querySelector('#specificAppSection');
    if (!specificAppSection) return setTimeout(addOptions, 1);
    if (document.querySelector('#soundSelector')) return;
    // 向界面中添加选项
    var soundSelector = document.createElement('ul'); soundSelector.id = 'soundSelector';
    Object.keys(sounds).map(function (name) {
      var li = document.createElement('li'); li.id = name + 'SoundSelector';
      var title = ['<strong>', sounds[name].text, '</strong>'].join('');
      var list = Object.keys(types).map(function (type) {
        return ['<label', (sounds[name][type] ? '' : ' for="' + name + 'SoundSelectorFile"'), '>',
          '<input type="radio" name="', name, 'SoundSelectorRadio" value="', type, '" />',
        types[type], '</label>'].join('');
      }).join('');
      var file = '<input type="file" id="' + name + 'SoundSelectorFile" ' +
        'accept="audio/*" style="width: 1px; opacity: 0;" />';
      li.innerHTML = title + list + file;
      // 处理某个选项被点击
      var loadFileOpt = null, loadFileL = null;
      var fileObj = li.querySelector('input[type="file"]');
      Object.keys(types).map(function (type) {
        var l = li.querySelector('input[value="' + type + '"]'); if (!l) return;
        if (type === sounds[name].chose) l.checked = true;
        if (sounds[name][type]) {
          l.addEventListener('click', function (e) {
            saveConfig(name, type);
            playSound(name);
          });
        } else {
          l.addEventListener('click', function (e) {
            e.preventDefault(); fileObj.click();
          }, true);
          loadFileOpt = type; loadFileL = l;
        }
      });
      // 读取自定义选项的文件
      fileObj.addEventListener('change', function (e) {
        var reader = new FileReader();
        var file = fileObj.files[0];
        reader.addEventListener('load', function () {
          loadFileL.checked = true;
          saveConfig(name, loadFileOpt, reader.result);
          playSound(name);
        });
        if (file) reader.readAsDataURL(file);
        e.stopPropagation();
      });
      // 点击时发出声音
      return soundSelector.appendChild(li);
    });
    specificAppSection.parentNode.insertBefore(soundSelector, specificAppSection);
  };

  // 检查用户是否打开了设置窗口
  var addConfigHandler = function () {
    if (!unsafeWindow.alloy.portal || !unsafeWindow.alloy.portal.runApp)
      return setTimeout(addSoundHandler, 1);
    var oldRunApp = unsafeWindow.alloy.portal.runApp;
    unsafeWindow.alloy.portal.runApp = function () {
      var args = Array.apply(Array, arguments);
      var ret = oldRunApp.apply(this, arguments);
      if (args[0] && args[0] === 'notifications') addOptions(args[0]);
      return ret;
    };
  };

  // 等待网页加载完毕
  var waitLoaded = (function () {
    var waiting = [];
    (function (callback) {
      var oldProgress = null;
      var func = function () {
        try { callback(); } catch (e) { }
      };
      var sEl = document.getElementById("startingTips");
      if (Number(sEl.innerHTML.slice(0, -1)) === 100) func();
      else {
        oldProgress = unsafeWindow.progress;
        unsafeWindow.progress = function (msg) {
          if (msg === 'portalReady') {
            func();
            unsafeWindow.progress = oldProgress;
          }
          oldProgress.apply(this, arguments);
        };
      }
    }(function () {
      waiting.forEach(function (f) { f(); });
      waiting = null;
    }));
    return function (callback) {
      if (waiting) waiting.push(callback);
      else callback();
    };
  }());

  // 添加样式
  var stylish = function () {
    GM_addStyle([
    '#notificationsArea #soundSelector li {',
      'background: #f0f0ef;',
      'border: 1px solid #ddd;',
      'margin: 0 0 8px 32px;',
      'text-indent: 11px;',
      'line-height: 35px;',
    '}',
    '#notificationsArea #soundSelector strong {',
      'color: #000000;',
      'font-size: 14px;',
      'margin-right: 9px;',
    '}',
    '#notificationsArea #soundSelector label {',
      'display: inline-block;',
      'width: 72px;',
    '}',
    // 修理下方应用程序的声音设置
    '#specificAppSection { height: calc(100% - 276px) !important; }',
    '#specificAppGrid { height: calc(100% - 30px) !important; }',
    '#specificAppGridBody { height: calc(100% - 32px) !important; }',
    ].join(''));
  };

  waitLoaded(addConfigHandler);
  waitLoaded(addSoundHandler);
  waitLoaded(stylish);

}());