if(document.URL.search(/\/feuerwachen\/\d+\/feuerwehrleute/) != -1){
	var formNode = document.createElement('form');
	var buttonNode = document.createElement('input');
	var hiddenNode = document.createElement('input');
	var wacheID = document.URL.match(/\d+/);

	formNode.setAttribute('action','/personal/jauntStart');
	formNode.setAttribute('method','post');
	formNode.setAttribute('name','jaunt');

	buttonNode.setAttribute('type','submit');
	buttonNode.setAttribute('class','button');
	buttonNode.setAttribute('name','commit');
	buttonNode.setAttribute('value','Ausflug starten');

	hiddenNode.setAttribute('type','hidden');
	hiddenNode.setAttribute('name','feuerwache_id');
	hiddenNode.setAttribute('id','feuerwache_id');
	hiddenNode.setAttribute('value',wacheID);
	
	formNode.appendChild(buttonNode);
	formNode.appendChild(hiddenNode);
	document.getElementById('content').appendChild(formNode);
}



